import styled from "styled-components"

export const Container = styled.div`
    display:flex;
    flex-direction: column;
    align-items: center;
    justify-content: flex-start;
    gap:15px;
    width:230px;
    height:350px;
    padding:10px;
    background-color: #E0E1DE;
    border-radius:8px;
    margin:5px;

    img{
        width:220px;
        height:190px;
    }
`
