import styled from "styled-components"
export const Span = styled.span`
font-size:50px;
color:white;
display:flex;
`

export const Container = styled.div`
width:400px;
height:400px;
display:flex;
align-items: center;
margin:auto;
margin-top:150px;
background-color:gray;
justify-content: center;
border-radius:10px;
border:20px solid gray;
`
