import { ADD_PRODUCT } from "./actionsTypes";
const products =[

    {name:"Smartphone S20 Fe",price: 10.00, image:"https://images-americanas.b2w.io/produtos/01/00/img/3234381/2/3234381205P.jpg"},
    {name:"teste",price: 1979.00, image:"link-teste"}

];

const productsReducer = (state = products, action) => {
    switch(action.type){
        case ADD_PRODUCT:
            const product = action.product;
            return[...state,product];
        default:
            return state;
    }
}
export default productsReducer;
